import React from "react";

import memesData from "../memesData";

export default function Meme() {

  const [memeImage, setMemeImage] = React.useState("");

  function getMemeImage() {
    let memesArray = memesData.data.memes;
    let randomNum = Math.floor(Math.random() * memesArray.length);
    // memesArray[randomNum].url;
    setMemeImage(prevState => prevState + memesArray[randomNum].url)
  }

  return (
    <main className="meme-generator">
      <div className="inputs">
        <input
          type="text"
          name="line-1"
          id="line-1"
          required
          minLength={1}
          placeholder="Top text"
        />
        <input
          type="text"
          name="line-2"
          id="line-2"
          required
          minLength={1}
          placeholder="Bottom text"
        />
      </div>
      <button type="submit" onClick={getMemeImage}>
        Get a new meme image ðŸ–¼
      </button>
      <div >
        <img src={memeImage} />
      </div>
    </main>
  );
}
