import React from "react";

import memesData from "../memesData";

export default function Meme() {

  const [meme, setMeme] = React.useState({
    topText: '',
    bottomText: '',
    memeImage: '' 
  });

  const [allMemesImages, setAllMemesImage] = React.useState(memesData);

  function getMemeImage() {
    let memesArray = allMemesImages.data.memes;
    let randomNum = Math.floor(Math.random() * memesArray.length);
    // memesArray[randomNum].url;
    setMeme(prevState => {
        return {
            ...prevState,
            memeImage: memesArray[randomNum].url
        }
    })
  }

  return (
    <main className="meme-generator">
      <div className="inputs">
        <input
          type="text"
          name="topText"
          id="line-1"
          required
          minLength={1}
          placeholder="Top text"
        />
        <input
          type="text"
          name="bottom"
          id="line-2"
          required
          minLength={1}
          placeholder="Bottom text"
        />
      </div>
      <button type="submit" onClick={getMemeImage}>
        Get a new meme image ðŸ–¼
      </button>
      <div className="memeImage">
        <img src={meme.memeImage} alt="" />
        <h2 className="meme--text top">One does not simply</h2>
        <h2 className="meme--text bottom">Walk into Mordor</h2>
      </div>
    </main>
  );
}
